sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},
	getDate: function(d){
		var date = new GlideDateTime(d);
		return date.getDate();
	},
	getLocalDate: function(d){
		var date = new GlideDateTime(d);
		return date.getLocalDate();
	},
	isDateToday: function(date){
		var rightnow = new GlideDateTime();
		var checkdate = new GlideDateTime(date);

// 		var rdate = rightnow.getDate().getValue();
// 		var cdate = checkdate.getDate().getValue();
// 		// If the testdate is before rightnow, return true, else return false
// 		if (!rdate.localeCompare(cdate)) {
// 		if (rdate==cdate) {
		var rdate = rightnow.getLocalDate();
		var cdate = checkdate.getLocalDate();
		if(rdate.compareTo(cdate) == 0){
			return true;
		}
		else {
			return false;
		}
	},

    type: 'NeedItUtils'
};
